# https://getgeoapi.com/#ip-lookup# https://rapidapi.com/natkapral/api/ip-geo-location/detailsimport requestsdef request_IPlocation(key,ip=None,format="json",language="en",filter=""):    # key = "4a5a849cbd38567fe6cc412a8ae218f8d167fad9"    # format can be either JSON or XML    if ip is None:        raise Exception("IP address cannot be None")            language_set = {"en","ru","zh","es","ar","fr","fa","ja","pl","it","pt","de"}    # All the available output languages     if language not in language_set:        raise Exception("output language can only be selected from en,ru,zh,es,ar,fr,fa,ja,pl,it,pt,de")            # filter_options = {"asn", "city", "country", "continent", "area", "currency", "security", "time", "postcode"}    # filter = "city,postcode"    # if filter defined not match keywords in filter_options set, then it will grab and present all the information.            parameters = {"api_key": key, "format": format}    url = "https://api.getgeoapi.com/api/v2/ip/" + ip + "?api_key=" + key + "&format=" + format + "&filter=" + filter + "language=" + language        try:        response = requests.get(url, parameters)    except Exception as e:        print(e)        return False    '''    if format == "json":        result = response.json()    elif format == "xml":        result = response.xml()    else:        try:            result = response.json()        except Exception as e:            print(e)            print("format setting is wrong, it can only be either 'json' or 'xml'")            return False    '''    result = response.text    return resultdef Requester_ip_lookup(key,format="json"):    url = "https://api.getgeoapi.com/api/v2/ip/check?api_key=" + key + "&format=" + format    # output format can be either JSON or XML    parameters = {"api_key": key, "format": format}    result = requests.get(url, parameters)    return result.json()